// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * @title OracleAttestationVerifier
 * @dev Verifies oracle attestations using Merkle proofs
 */
contract OracleAttestationVerifier is AccessControl {
    
    bytes32 public constant ORACLE_ROLE = keccak256("ORACLE_ROLE");
    
    struct OracleData {
        string oracleType;      // "existence", "ownership", "activity"
        bytes32 dataHash;       // Hash of oracle result
        uint256 timestamp;
        address attestor;
    }
    
    // Merkle root => valid
    mapping(bytes32 => bool) public validAttestations;
    
    // Oracle address => authorized
    mapping(address => bool) public authorizedOracles;
    
    event AttestationStored(bytes32 indexed merkleRoot, uint256 timestamp);
    event OracleAuthorized(address indexed oracle, bool authorized);
    
    constructor(address admin) {
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
    }
    
    /**
     * @dev Store oracle attestation Merkle root
     */
    function storeAttestation(bytes32 merkleRoot) external onlyRole(ORACLE_ROLE) {
        validAttestations[merkleRoot] = true;
        emit AttestationStored(merkleRoot, block.timestamp);
    }
    
    /**
     * @dev Verify a specific oracle result against stored Merkle root
     */
    function verifyOracleResult(
        bytes32 merkleRoot,
        bytes32 leaf,
        bytes32[] calldata proof
    ) external view returns (bool) {
        require(validAttestations[merkleRoot], "Invalid attestation root");
        return MerkleProof.verify(proof, merkleRoot, leaf);
    }
    
    /**
     * @dev Authorize oracle address
     */
    function authorizeOracle(address oracle, bool authorized) 
        external 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        authorizedOracles[oracle] = authorized;
        _grantRole(ORACLE_ROLE, oracle);
        emit OracleAuthorized(oracle, authorized);
    }
}

contracts/redemption/AsyncRedemptionCont.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "../tokens/RWASecurityToken.sol";

/**
 * @title AsyncRedemptionController
 * @dev ERC-7540 async redemption for illiquid RWA
 * @notice Handles multi-step redemption process
 */
contract AsyncRedemptionController is AccessControl, ReentrancyGuard {
    
    bytes32 public constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");
    
    enum RedemptionStatus {
        None,
        Requested,
        Processing,
        Approved,
        Settled,
        Cancelled
    }
    
    struct RedemptionRequest {
        address investor;
        address token;
        uint256 tokenAmount;
        uint256 estimatedValue;  // In base currency
        uint256 requestTime;
        uint256 settlementTime;
        RedemptionStatus status;
        bytes32 proofHash;       // Hash of liquidation proof
    }
    
    // Request ID => Request
    mapping(bytes32 => RedemptionRequest) public redemptionRequests;
    
    // Token => Total pending redemptions
    mapping(address => uint256) public pendingRedemptions;
    
    // Investor => Array of request IDs
    mapping(address => bytes32[]) public investorRequests;
    
    uint256 public constant SETTLEMENT_PERIOD = 90 days; // Max wait time
    
    // Events
    event RedemptionRequested(
        bytes32 indexed requestId,
        address indexed investor,
        address indexed token,
        uint256 amount,
        uint256 timestamp
    );
    
    event RedemptionApproved(bytes32 indexed requestId, uint256 settlementValue);
    event RedemptionSettled(bytes32 indexed requestId, uint256 actualValue);
    event RedemptionCancelled(bytes32 indexed requestId, string reason);
    
    constructor(address admin) {
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
    }
    
    /**
     * @dev Request redemption
     */
    function requestRedemption(
        address token,
        uint256 amount
    ) external nonReentrant returns (bytes32) {
        require(amount > 0, "Invalid amount");
        
        RWASecurityToken securityToken = RWASecurityToken(token);
        require(securityToken.balanceOf(msg.sender) >= amount, "Insufficient balance");
        
        // Generate request ID
        bytes32 requestId = keccak256(
            abi.encodePacked(msg.sender, token, amount, block.timestamp)
        );
        
        // Calculate estimated value (NAV per token * amount)
        uint256 navPerToken = securityToken.navPerToken();
        uint256 estimatedValue = (navPerToken * amount) / 1e18;
        
        // Lock tokens
        securityToken.transferFrom(msg.sender, address(this), amount);
        
        // Create request
        redemptionRequests[requestId] = RedemptionRequest({
            investor: msg.sender,
            token: token,
            tokenAmount: amount,
            estimatedValue: estimatedValue,
            requestTime: block.timestamp,
            settlementTime: 0,
            status: RedemptionStatus.Requested,
            proofHash: bytes32(0)
        });
        
        pendingRedemptions[token] += amount;
        investorRequests[msg.sender].push(requestId);
        
        emit RedemptionRequested(requestId, msg.sender, token, amount, block.timestamp);
        
        return requestId;
    }
    
    /**
     * @dev Approve redemption (after off-chain liquidation)
     */
    function approveRedemption(
        bytes32 requestId,
        uint256 settlementValue,
        bytes32 proofHash
    ) external onlyRole(OPERATOR_ROLE) {
        RedemptionRequest storage request = redemptionRequests[requestId];
        require(request.status == RedemptionStatus.Requested, "Invalid status");
        
        request.status = RedemptionStatus.Approved;
        request.estimatedValue = settlementValue;
        request.proofHash = proofHash;
        
        emit RedemptionApproved(requestId, settlementValue);
    }
    
    /**
     * @dev Settle redemption (transfer funds to investor)
     */
    function settleRedemption(bytes32 requestId) 
        external 
        onlyRole(OPERATOR_ROLE) 
        nonReentrant 
    {
        RedemptionRequest storage request = redemptionRequests[requestId];
        require(request.status == RedemptionStatus.Approved, "Not approved");
        
        // Burn tokens
        RWASecurityToken token = RWASecurityToken(request.token);
        token.burn(request.tokenAmount);
        
        // Mark as settled
        request.status = RedemptionStatus.Settled;
        request.settlementTime = block.timestamp;
        
        pendingRedemptions[request.token] -= request.tokenAmount;
        
        emit RedemptionSettled(requestId, request.estimatedValue);
        
        // Note: Actual fund transfer happens off-chain (bank transfer, etc.)
    }
    
    /**
     * @dev Cancel redemption
     */
    function cancelRedemption(bytes32 requestId, string calldata reason) 
        external 
        onlyRole(OPERATOR_ROLE) 
    {
        RedemptionRequest storage request = redemptionRequests[requestId];
        require(
            request.status == RedemptionStatus.Requested || 
            request.status == RedemptionStatus.Approved,
            "Cannot cancel"
        );
        
        // Return tokens to investor
        RWASecurityToken token = RWASecurityToken(request.token);
        token.transfer(request.investor, request.tokenAmount);
        
        request.status = RedemptionStatus.Cancelled;
        pendingRedemptions[request.token] -= request.tokenAmount;
        
        emit RedemptionCancelled(requestId, reason);
    }
    
    /**
     * @dev Get redemption request
     */
    function getRedemptionRequest(bytes32 requestId) 
        external 
        view 
        returns (RedemptionRequest memory) 
    {
        return redemptionRequests[requestId];
    }
    
    /**
     * @dev Get investor requests
     */
    function getInvestorRequests(address investor) 
        external 
        view 
        returns (bytes32[] memory) 
    {
        return investorRequests[investor];
    }
}

contracts/TokenFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "./tokens/RWASecurityToken.sol";
import "./AssetRegistry.sol";

/**
 * @title TokenFactory
 * @dev Factory for deploying security tokens linked to registered assets
 */
contract TokenFactory is AccessControl {
    
    bytes32 public constant FACTORY_ROLE = keccak256("FACTORY_ROLE");
    
    AssetRegistry public assetRegistry;
    
    // Fingerprint => Token address
    mapping(bytes32 => address) public assetTokens;
    
    // All deployed tokens
    address[] public deployedTokens;
    
    event TokenDeployed(
        bytes32 indexed fingerprint,
        address indexed tokenAddress,
        string name,
        string symbol,
        uint256 timestamp
    );
    
    constructor(address _assetRegistry, address admin) {
        assetRegistry = AssetRegistry(_assetRegistry);
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(FACTORY_ROLE, admin);
    }
    
    /**
     * @dev Deploy new security token for a registered asset
     */
    function deployToken(
        bytes32 fingerprint,
        string memory name,
        string memory symbol,
        string memory description,
        uint256 totalAssetValue
    ) external onlyRole(FACTORY_ROLE) returns (address) {
        require(assetRegistry.isEligible(fingerprint), "Asset not eligible");
        require(assetTokens[fingerprint] == address(0), "Token already exists");
        
        RWASecurityToken token = new RWASecurityToken(
            name,
            symbol,
            address(assetRegistry),
            fingerprint,
            description,
            totalAssetValue
        );
        
        assetTokens[fingerprint] = address(token);
        deployedTokens.push(address(token));
        
        // Mark asset as tokenized in registry
        // Note: TokenFactory must have DEFAULT_ADMIN_ROLE on AssetRegistry for this to work
        assetRegistry.markAsTokenized(fingerprint, address(token));
        
        // Grant admin rights to the deployer (so they can manage compliance/minting)
        token.grantRole(token.DEFAULT_ADMIN_ROLE(), msg.sender);
        token.grantRole(token.MINTER_ROLE(), msg.sender);
        token.grantRole(token.COMPLIANCE_ROLE(), msg.sender);
        
        emit TokenDeployed(fingerprint, address(token), name, symbol, block.timestamp);
        
        return address(token);
    }
    
    /**
     * @dev Get total deployed tokens
     */
    function totalTokens() external view returns (uint256) {
        return deployedTokens.length;
    }
}

contracts/tokens/RWASecurityToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title RWASecurityToken
 * @dev ERC-3643 compliant security token for RWA
 * @notice Implements identity-based transfers with compliance checks
 */
contract RWASecurityToken is ERC20, AccessControl, ReentrancyGuard {
    
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant COMPLIANCE_ROLE = keccak256("COMPLIANCE_ROLE");
    bytes32 public constant AGENT_ROLE = keccak256("AGENT_ROLE");
    
    // Asset registry reference
    address public assetRegistry;
    bytes32 public assetFingerprint;
    
    // Identity registry (simplified - use full T-REX in production)
    mapping(address => bool) public verifiedInvestors;
    mapping(address => bool) public frozenWallets;
    
    // Compliance rules
    uint256 public maxInvestorsCount;
    uint256 public currentInvestorsCount;
    mapping(address => bool) public isInvestor;
    
    // Token-specific data
    struct TokenInfo {
        string assetDescription;
        uint256 totalAssetValue;  // NAV in base currency
        uint256 creationDate;
        bool paused;
    }
    
    TokenInfo public tokenInfo;
    
    // Events
    event InvestorVerified(address indexed investor, bool verified);
    event WalletFrozen(address indexed wallet, bool frozen);
    event ComplianceRuleUpdated(string rule, uint256 value);
    event ForcedTransfer(address indexed from, address indexed to, uint256 amount, address indexed agent);
    
    constructor(
        string memory name,
        string memory symbol,
        address _assetRegistry,
        bytes32 _assetFingerprint,
        string memory _assetDescription,
        uint256 _totalAssetValue
    ) ERC20(name, symbol) {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);
        
        assetRegistry = _assetRegistry;
        assetFingerprint = _assetFingerprint;
        
        tokenInfo = TokenInfo({
            assetDescription: _assetDescription,
            totalAssetValue: _totalAssetValue,
            creationDate: block.timestamp,
            paused: false
        });
        
        maxInvestorsCount = 2000; // Default limit
    }
    
    /**
     * @dev Mint tokens (NAV-based issuance)
     */
    function mint(address to, uint256 amount) 
        external 
        onlyRole(MINTER_ROLE) 
        nonReentrant 
    {
        require(!tokenInfo.paused, "Token is paused");
        require(verifiedInvestors[to], "Investor not verified");
        require(!frozenWallets[to], "Wallet is frozen");
        
        if (!isInvestor[to] && amount > 0) {
            require(currentInvestorsCount < maxInvestorsCount, "Max investors reached");
            isInvestor[to] = true;
            currentInvestorsCount++;
        }
        
        _mint(to, amount);
    }
    
    /**
     * @dev Burn tokens (redemption)
     */
    function burn(uint256 amount) external {
        require(!tokenInfo.paused, "Token is paused");
        _burn(msg.sender, amount);
        
        if (balanceOf(msg.sender) == 0 && isInvestor[msg.sender]) {
            isInvestor[msg.sender] = false;
            currentInvestorsCount--;
        }
    }
    
    /**
     * @dev Override transfer to include compliance checks
     */
    function transfer(address to, uint256 amount) 
        public 
        override 
        returns (bool) 
    {
        require(_canTransfer(msg.sender, to, amount), "Transfer not compliant");
        return super.transfer(to, amount);
    }
    
    /**
     * @dev Override transferFrom to include compliance checks
     */
    function transferFrom(address from, address to, uint256 amount) 
        public 
        override 
        returns (bool) 
    {
        require(_canTransfer(from, to, amount), "Transfer not compliant");
        return super.transferFrom(from, to, amount);
    }
    
    /**
     * @dev Forced transfer (legal recovery, court order)
     */
    function forcedTransfer(
        address from,
        address to,
        uint256 amount,
        string calldata reason
    ) external onlyRole(AGENT_ROLE) {
        require(bytes(reason).length > 0, "Reason required");
        _transfer(from, to, amount);
        emit ForcedTransfer(from, to, amount, msg.sender);
    }
    
    /**
     * @dev Verify investor
     */
    function verifyInvestor(address investor, bool verified) 
        external 
        onlyRole(COMPLIANCE_ROLE) 
    {
        verifiedInvestors[investor] = verified;
        emit InvestorVerified(investor, verified);
    }
    
    /**
     * @dev Freeze wallet
     */
    function freezeWallet(address wallet, bool frozen) 
        external 
        onlyRole(COMPLIANCE_ROLE) 
    {
        frozenWallets[wallet] = frozen;
        emit WalletFrozen(wallet, frozen);
    }
    
    /**
     * @dev Pause token
     */
    function pause(bool paused) external onlyRole(DEFAULT_ADMIN_ROLE) {
        tokenInfo.paused = paused;
    }
    
    /**
     * @dev Update NAV
     */
    function updateNAV(uint256 newNAV) external onlyRole(DEFAULT_ADMIN_ROLE) {
        tokenInfo.totalAssetValue = newNAV;
    }
    
    /**
     * @dev Internal compliance check
     */
    function _canTransfer(address from, address to, uint256 amount) 
        internal 
        view 
        returns (bool) 
    {
        if (tokenInfo.paused) return false;
        if (frozenWallets[from] || frozenWallets[to]) return false;
        if (!verifiedInvestors[from] || !verifiedInvestors[to]) return false;
        if (!isInvestor[to] && currentInvestorsCount >= maxInvestorsCount) return false;
        
        return true;
    }
    
    /**
     * @dev Get current NAV per token
     */
    function navPerToken() external view returns (uint256) {
        uint256 supply = totalSupply();
        if (supply == 0) return 0;
        return (tokenInfo.totalAssetValue * 1e18) / supply;
    }
}

